mapper.select:
select * from ( select row_number() over (order by emp_num) as rn,
from employees
emp_name like '%' || #{searchWord} || '%' or emp_num like '%' || #{searchWord} || '%'
) where rn between #{startRow} and #{endRow}

mapper.update:
update employees
set emp_id = #{empId}
	,emp_name = #{empName}
	,emp_addr = #{empAddr}
	,emp_addr_detail = #{empAddrDetail}
	,EMP_POST = #{empPost}
	,EMP_PHONE = #{empPhone}
	,EMP_EMAIL = #{empEmail}
	,EMP_IMAGE = #{empImage}
where emp_num = #{empNum}
